import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import axios from "axios";
import { useEffect, useState } from "react";
import {
  img_500,
  unavailable,
  unavailableLandscape,
} from "../../config/config";
import { Button } from "@material-ui/core";
import YouTubeIcon from "@material-ui/icons/YouTube";
import "./ContentModal.css";
import Carousel from "../Carousel/Carousel";
const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    width: "90%",
    height: "80%",
    backgroundColor: "#39445a",
    border: "1px solid #282c34",
    borderRadius: 10,
    color: "white",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(1, 1, 3),
  },
}));

export default function ContentModal({ children, media_type, id }) {
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [content, setContent] = useState();
  const [video, setVideo] = useState();

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  const fetchData = async () => {
    const { data } = await axios.get(
      // `https://api.themoviedb.org/3/${media_type}/${id}?api_key=89c1beb1c605b68a9af79953628d7745&language=en-US`
      "http://www.omdbapi.com/?i=${id}&apikey=d310343"
    );

    setContent(data);
  };
  const fetchVideo = async () => {
    const { data } = await axios.get(
      // `https://api.themoviedb.org/3/${media_type}/${id}/videos?api_key=89c1beb1c605b68a9af79953628d7745&language=en-US`
      `http://www.omdbapi.com/?i=${id}&apikey=d310343`
    );

    setVideo(data.results[0]?.key);
  };
  useEffect(() => {
    fetchData();
    fetchVideo();
    // eslint-disable-next-line
  }, []);

  return (
    <div>
      <div type="button" className="media" onClick={handleOpen}>
        {children}
      </div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          {content && (
            <div className={classes.paper}>
              <div className="ContentModal">
                <img
                  src={content.poster ? `${content.poster}` : unavailable}
                  alt={content.name || content.Title}
                  className="ContentModal__portrait"
                />
                {/* <img
                  src={
                    content.backdrop_path
                      ? `${img_500}/${content.backdrop_path}`
                      : unavailableLandscape
                  }
                  alt={content.name || content.Title}
                  className="ContentModal__landscape"
                /> */}
                <div className="ContentModal__about">
                  <span className="ContentModal__title">
                    {content.name || content.Title} (
                    {(
                      content.Year ||
                      content.release_date ||
                      "-----dfghj"
                    ).substring(0, 4)}
                    )
                  </span>
                  {content.tagline && (
                    <i className="tagline">{content.tagline}</i>
                  )}

                  <span className="ContentModal__description">
                    {content.overview}
                  </span>

                  <div>
                    <Carousel id={id} media_type={media_type} />
                  </div>

                  {/* <Button
                    variant="contained"
                    startIcon={<YouTubeIcon />}
                    color="primary"
                    target="__blank"
                    href={`https://www.youtube.com/watch?v=${video}`}
                  >
                    Enjoy the Trailer
                  </Button> */}
                </div>
              </div>
            </div>
          )}
        </Fade>
      </Modal>
    </div>
  );
}
