import React from "react";
import { Chip } from "@material-ui/core";
const staticGenres = [
  { id: 1, name: "1980-1985" },
  { id: 2, name: "1985-1990" },
  { id: 3, name: "1990-1995" },
  { id: 4, name: "1995-2000" },
  { id: 5, name: "2000-2005" },
  { id: 6, name: "2005-2010" },
  { id: 7, name: "2010-2015" },
  { id: 8, name: "2015-2020" },
  { id: 9, name: "2020-2025" },
  // Add more genres as needed
];
const Genres = ({
  selectedGenres,
  setSelectedGenres,
  setPage,
  genres,
  setGenres,
}) => {
  const handleAdd = (genre) => {
    setSelectedGenres([...selectedGenres, genre]);
    setPage(1);
  };

  const handleRemove = (genre) => {
    setSelectedGenres(
      selectedGenres.filter((selected) => selected.id !== genre.id)
    );
    setPage(1);
  };

  return (
    <div style={{ padding: "6px 0" }}>
      {selectedGenres &&
        selectedGenres.map((genre) => (
          <Chip
            style={{ margin: 2 }}
            label={genre.name}
            key={genre.id}
            color="primary"
            clickable
            size="small"
            onDelete={() => handleRemove(genre)}
          />
        ))}
      {staticGenres &&
        staticGenres.map((genre) => (
          <Chip
            style={{ margin: 2 }}
            label={genre.name}
            key={genre.id}
            clickable
            size="small"
            onClick={() => handleAdd(genre)}
          />
        ))}
    </div>
  );
};

export default Genres;
