import axios from "axios";
import "./Trending.css";
import { useEffect, useState } from "react";
import SingleContent from "../../components/SingleContent/SingleContent";
import CustomPagination from "../../components/Pagination/CustomPagination";
import Genres from "../../components/Genres/Genres";
import useGenre from "../../hooks/useGenre";

const Trending = () => {
  const [page, setPage] = useState(1);
  const [content, setContent] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const genreforURL = useGenre(selectedGenres);

  const fetchTrending = async () => {
    const apiKey = "d310343"; // Your OMDb API key
    const searchTerm = "Inception";
    // const page = 1; // Pagination page number
    const { data } = await axios.get(
      // `https://api.themoviedb.org/3/trending/all/day?api_key=89c1beb1c605b68a9af79953628d7745&page=${page}`
      `http://www.omdbapi.com/?s=${encodeURIComponent(
        searchTerm
      )}&page=${page}&apikey=${apiKey}`
    );
    console.log("ankit", data);

    setContent(data.Search);
  };

  useEffect(() => {
    window.scroll(0, 0);
    fetchTrending();
    // eslint-disable-next-line
  }, [page, genreforURL]);

  return (
    <div>
      <span className="pageTitle"> ðŸ˜Ž Trending ðŸ˜œ </span>
      {console.log("ast", selectedGenres)}
      <Genres
        type="tv"
        selectedGenres={selectedGenres}
        setSelectedGenres={setSelectedGenres}
        genres={genres}
        setGenres={setGenres}
        setPage={setPage}
      />
      <div className="trending">
        {content &&
          content.map((c) => (
            <SingleContent
              key={c.imdbID}
              id={c.imdbID}
              poster={c.Poster}
              title={c.Title || c.name}
              date={c.first_air_date || c.Year}
              media_type={c.Type}
              vote_average={c.vote_average}
            />
          ))}
      </div>
      <CustomPagination setPage={setPage} />
    </div>
  );
};

export default Trending;
