        <p>No saved movies found!</p>
      ) : (
        <div>
          <span className="pageTitle">ğŸ”¥ğŸ”¥Enjoy your best SeriesğŸ”¥ğŸ”¥</span>

          <div className="trending">
            {savedMovies &&
              savedMovies.map((c) => (
                <SingleContent
                  key={c.imdbID}
                  id={c.imdbID}
                  poster={c.Poster}
                  title={c.Title || c.name}
                  date={c.first_air_date || c.Year}
                  media_type={c.Type}
                  vote_average={c.vote_average}
                />
              ))}
          </div>
          {/* {numOfPages > 1 && (
        <CustomPagination setPage={setPage} numOfPages={numOfPages} />
      )} */}
        </div>
