import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom"; // If you are using React Router for navigation
//import "./SavedMoviesPage.css"; // Optional: for custom styling

export default function SavedMoviesPage() {
  const [savedMovies, setSavedMovies] = useState([]);

  // Fetch saved movies from localStorage
  useEffect(() => {
    const savedMoviesFromStorage =
      JSON.parse(localStorage.getItem("savedMovies")) || [];
    setSavedMovies(savedMoviesFromStorage);
  }, []);

  return (
    <div className="saved-movies-page">
      <h1>Saved Movies</h1>
      {savedMovies.length === 0 ? (
        <p>No saved movies found!</p>
      ) : (
        <div className="saved-movies-list">
          {savedMovies.map((movie) => (
            <div key={movie.imdbID} className="saved-movie">
              <img
                src={
                  movie.Poster !== "N/A"
                    ? movie.Poster
                    : "https://via.placeholder.com/500"
                }
                alt={movie.Title}
                className="saved-movie-poster"
              />
              <div className="saved-movie-details">
                <h2>{movie.Title}</h2>
                <p>Year: {movie.Year}</p>
                <Link to={`/movie/${movie.imdbID}`}>View Details</Link>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
