import axios from "axios";
import "./Trending.css";
import { useEffect, useState } from "react";
import SingleContent from "../../components/SingleContent/SingleContent";
import CustomPagination from "../../components/Pagination/CustomPagination";
import Genres from "../../components/Genres/Genres";
import useGenre from "../../hooks/useGenre";

const Trending = () => {
  const [page, setPage] = useState(1);
  const [content, setContent] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const genreforURL = useGenre(selectedGenres);
  console.log("hello", genreforURL);
  const fetchTrending = async (year) => {
    // const apiKey = "d310343"; // Your OMDb API key
    // const searchTerm = "Inception";
    // // const page = 1; // Pagination page number
    // const { data } = await axios.get(

    //   // `https://api.themoviedb.org/3/trending/all/day?api_key=89c1beb1c605b68a9af79953628d7745&page=${page}`
    //   `http://www.omdbapi.com/?s=${encodeURIComponent(
    //     searchTerm
    //   )}&page=${page}&apikey=${apiKey}`
    // );
    console.log("gev", genreforURL);
    const apiKey = "d310343"; // Replace with your actual OMDb API key
    // const year = "2021"; // Example year filter
    const page = 1; // Pagination if needed
    const url = `http://www.omdbapi.com/?apikey=${apiKey}&s=movie&type=movie&y=${year}&page=${page}`;
    const { data } = await axios.get(
      `http://www.omdbapi.com/?apikey=${apiKey}&s=movie&type=movie&y=${year}&page=${page}`
    );

    return data.Search;
    // setContent(data.Search);
  };
  // const fetchMoviesByYear = async (year, page = 1) => {
  //   try {
  //     const url = `http://www.omdbapi.com/?apikey=${apiKey}&s=movie&type=movie&y=${year}&page=${page}`;
  //     const { data } = await axios.get(url);

  //     if (data.Response === 'True') {
  //       return data.Search;
  //     } else {
  //       console.error(`No results for year ${year}:`, data.Error);
  //       return [];
  //     }
  //   } catch (error) {
  //     console.error(`Error fetching data for year ${year}:`, error);
  //     return [];
  //   }
  // };
  const fetchMoviesByYearRanges = async (ranges) => {
    let combinedResults = [];

    for (const range of ranges) {
      const [startYear, endYear] = range;
      for (let year = startYear; year <= endYear; year++) {
        const movies = await fetchTrending(year);
        combinedResults = [...combinedResults, ...movies];
      }
    }

    return combinedResults;
  };
  const fetchTrendingt = async () => {
    const parseYearRanges = (yearRangeString) => {
      return yearRangeString.split(",").map((range) => {
        const [start, end] = range.split("-").map((year) => parseInt(year, 10));
        return [start, end];
      });
    };
    const yearRanges = parseYearRanges(genreforURL);
    // const yearRanges = [
    //   [2002, 2005], // First range
    //   [2015, 2020], // Second range
    // ];

    const movies = await fetchMoviesByYearRanges(yearRanges);
    setContent(movies);
    // Combined array of movie results for all ranges
  };

  useEffect(() => {
    window.scroll(0, 0);
    // fetchTrending();
    fetchTrendingt();
    // eslint-disable-next-line
  }, [page, genreforURL]);

  return (
    <div>
      <span className="pageTitle"> 😎 Trending 😜 </span>
      {console.log("ast", selectedGenres)}
      <Genres
        type="tv"
        selectedGenres={selectedGenres}
        setSelectedGenres={setSelectedGenres}
        genres={genres}
        setGenres={setGenres}
        setPage={setPage}
      />
      <div className="trending">
        {content &&
          content.map((c) => (
            <SingleContent
              key={c.imdbID}
              id={c.imdbID}
              poster={c.Poster}
              title={c.Title || c.name}
              date={c.first_air_date || c.Year}
              media_type={c.Type}
              vote_average={c.vote_average}
            />
          ))}
      </div>
      <CustomPagination setPage={setPage} />
    </div>
  );
};

export default Trending;
